package org.trs.oa.controller;import org.trs.oa.entity.TsApproval;import org.trs.oa.entity.UserDocInfo;import org.trs.oa.exception.TimesheetException;import org.trs.oa.service.*;import org.trs.oa.utility.Constants;import org.trs.oa.utility.FileUtils;import org.trs.oa.vo.TimeSheetListItemVO;import org.apache.log4j.Logger;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.format.annotation.DateTimeFormat;import org.springframework.stereotype.Controller;import org.springframework.ui.ModelMap;import org.springframework.web.bind.annotation.PathVariable;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestMethod;import org.springframework.web.bind.annotation.RequestParam;import org.springframework.web.servlet.mvc.support.RedirectAttributes;import javax.servlet.http.HttpServletRequest;import javax.servlet.http.HttpServletResponse;import javax.servlet.http.HttpSession;import java.util.Date;import java.util.List;import java.util.Map;@Controller@RequestMapping({"/timesheet"})public class TimesheetController {    private final static Logger LOGGER = Logger.getLogger(TimesheetController.class);    @Autowired    ProjectService projectService;    @Autowired    TimesheetService timesheetService;    @Autowired    PDFReportService reportService;    @Autowired    MessageService msg;    @RequestMapping(value = "/ts", method = RequestMethod.GET)    public String allTimesheet(ModelMap model, HttpSession session) {        int userId = (int) session.getAttribute(Constants.USER_ID);        List<TimeSheetListItemVO> tsList = timesheetService.getTimesheetListForUser(userId);        model.addAttribute("tsList", tsList);        return "timesheet/timesheetList";    }    @RequestMapping(value = "/ts/{weekId}", method = RequestMethod.GET)    public String newTimesheet(ModelMap model, @PathVariable Integer weekId, HttpSession session) {        int userId = (int) session.getAttribute(Constants.USER_ID);        model.addAttribute("projList", projectService.getProjListForMap());        model.addAttribute("dateList", timesheetService.getCurrentTimesheetDate(weekId));        model.addAttribute("projTsList", timesheetService.getProjTimesheetData(weekId, userId));        TsApproval approvalStatus = timesheetService.getTimesheetStatus(weekId,userId);        if(approvalStatus == null){            model.addAttribute("editable",true);        }else{            model.addAttribute("ts_status",approvalStatus.getStatus().getValue());            model.addAttribute("ts_status_id",approvalStatus.getStatus().getId());            model.addAttribute("status_comment",approvalStatus.getComment());            model.addAttribute("submitterId",approvalStatus.getSubmitter().getId());            if (approvalStatus.getStatus().getId() == Constants.DEFAULT_TS_DENIED_STATUS_ID) {                model.addAttribute("editable",true);            }            model.addAttribute("histList",timesheetService.getTimesheetHistory(approvalStatus.getId()));        }        model.addAttribute(weekId);        return "timesheet/timesheet";    }    /**     * allInputs format:     * key : ${projTs.project.id}_${billCodeTs.billCode.id}_monday     * value : # of hours     *     * @param model     * @param dateOfMonday     * @param allInputs     * @return     */    @RequestMapping(value = "/save/{weekId}", method = RequestMethod.POST)    public String saveTimesheet(ModelMap model, @PathVariable Integer weekId,                               @DateTimeFormat(pattern = "MM/dd/yyyy") Date dateOfMonday,                               @RequestParam Map<String, String> allInputs,                               final RedirectAttributes redirectAttributes, HttpSession session) {        allInputs.remove("dateOfMonday");        allInputs.remove("_csrf");        allInputs.remove("statusId");        if (allInputs.size() < 1) {            LOGGER.debug("Empty Input, skip and return");            return "redirect:/timesheet/ts/" + weekId;        }        try {            timesheetService.saveTs(dateOfMonday, allInputs, (int) session.getAttribute(Constants.USER_ID));        } catch (TimesheetException e) {            LOGGER.debug(e);            e.printStackTrace();            redirectAttributes.addFlashAttribute("errorMsg", msg.getMessage("ts_save_fail", null));            return "redirect:/timesheet/ts/" + weekId;        }        redirectAttributes.addFlashAttribute("successMsg", msg.getMessage("ts_save_success", null));        return "redirect:/timesheet/ts/" + weekId;    }    @RequestMapping(value = "/saveUpdate/{weekId}", method = RequestMethod.POST)    public String saveAndSubmitTs(ModelMap model, @PathVariable Integer weekId,                                  @DateTimeFormat(pattern = "MM/dd/yyyy") Date dateOfMonday,                                  @RequestParam Map<String, String> allInputs,                                  final RedirectAttributes redirectAttributes, HttpSession session, Integer statusId){        boolean isResubmit = false;        if (statusId != null) {            if (Constants.DEFAULT_TS_DENIED_STATUS_ID == statusId) {                isResubmit = true;            }else if(Constants.DEFAULT_TS_APPROVED_STATUS_ID == statusId){                throw new RuntimeException("Cannot resubmit approved timesheet");            }        }        String res = saveTimesheet(model, weekId, dateOfMonday, allInputs, redirectAttributes, session);        timesheetService.submitTs(dateOfMonday, (int) session.getAttribute(Constants.USER_ID), isResubmit);        return res;    }    @RequestMapping("/manage")    public String manageTimeSheet(ModelMap model) {        model.addAttribute("projList", projectService.getProjListForMap());        return "/timesheet/manage_time_sheet";    }    @RequestMapping("/approval")    public String showApproveTimeSheet(ModelMap model, HttpSession session) {        model.addAttribute("tsToApproveList", timesheetService.getPendingSubmittedTs((int) session.getAttribute(Constants.USER_ID)));        return "/timesheet/approve_time_sheet";    }    @RequestMapping("/approval/approve")    public String approveTimeSheet(ModelMap model, HttpSession session, final RedirectAttributes redirectAttributes, Integer approvalId, String comment) {        int userId = (int) session.getAttribute(Constants.USER_ID);        timesheetService.approveOrDenyTs(userId, approvalId, comment, true);        model.addAttribute("tsToApproveList", timesheetService.getPendingSubmittedTs(userId));        redirectAttributes.addFlashAttribute("successMsg", msg.getMessage("ts_approved_success", null));        return "redirect:/timesheet/approval";    }    @RequestMapping("/approval/deny")    public String denyTimeSheet(ModelMap model, HttpSession session, final RedirectAttributes redirectAttributes, Integer approvalId, String comment) {        int userId = (int) session.getAttribute(Constants.USER_ID);        timesheetService.approveOrDenyTs(userId, approvalId, comment, false);        model.addAttribute("tsToApproveList", timesheetService.getPendingSubmittedTs(userId));        redirectAttributes.addFlashAttribute("successMsg", msg.getMessage("ts_denied_success", null));        return "redirect:/timesheet/approval";    }    @Autowired    DocumentService documentService;    @RequestMapping(value = "/export/pdf", method = RequestMethod.POST)    public void export(Integer weekId, Integer userId, HttpServletRequest request,HttpServletResponse response) {        try {            Integer fileId = reportService.generateTimeSheetReport(userId, weekId);            UserDocInfo docInfo = documentService.getUserDocInfoById(fileId);            FileUtils.fileDownload(docInfo.getFileLocation(),"timesheet.pdf",response, request);        } catch (Exception e) {            e.printStackTrace();            LOGGER.error(e);        }    }}